package com.banana.y17_2.promo;

import android.content.Context;
import android.os.Bundle;
import android.support.v4.app.Fragment;
import android.view.LayoutInflater;
import android.view.View;
import android.view.ViewGroup;
import android.widget.Button;
import android.widget.ImageView;
import android.widget.RelativeLayout;
import android.widget.TextView;

import com.bumptech.glide.Glide;

public class Fragment2 extends Fragment{

    RelativeLayout Root_Container;
    TextView Product_Name;
    TextView Product_Category;
    ImageView Product_Preview;
    TextView My_Account_Button;
    ImageView Filters_Button;
    Button Discover_More_Buton;

    String itemUrl;
    String ItemName;
    String ItemCategory;

    @Override
    public View onCreateView(LayoutInflater inflater, ViewGroup container,
                             Bundle savedInstanceState) {
        Context context = getContext();
        //создаем главную вьюшку
        View view = inflater.inflate(R.layout.fragment2, container, false);



        Root_Container = view.findViewById(R.id.Root_container);
        Product_Name = view.findViewById(R.id.Product_Name);
        Product_Category = view.findViewById(R.id.Product_Category);
        Product_Preview = view.findViewById(R.id.Product_Preview);
        My_Account_Button = view.findViewById(R.id.My_Account_Button);
        Filters_Button = view.findViewById(R.id.Filters_Button);
        Discover_More_Buton = view.findViewById(R.id.Discover_More_Button);



        itemUrl = "data:image/jpeg;base64,";
        ItemCategory = "is here yet";
        ItemName = "Sorry, nothing";


        Glide.with(this).load(itemUrl).into(Product_Preview);
        Product_Name.setText(ItemName);
        Product_Category.setText("-" + ItemCategory);
        return view;
    }
}
